import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { environment } from '@environment';
import { ChapterService } from '../../services/chapter.service';
import { LangService } from '../../services/lang.service';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrl: './main.component.scss'
})
export class MainComponent {

  public langs: string[] = environment.availableLangs;

  public chapters: string[] = environment.availableChapters;

  public env: string = environment.name;

  constructor(private lang: LangService, private chapter: ChapterService, private route: ActivatedRoute) {}

  public changeLang(lang: string): void {
    console.log(this.route.snapshot.url);
    this.lang.changeLang(lang, this.route.snapshot.url);
  }

  public isLangDisabled(lang: string): boolean {
    return this.lang.getCurrentLang() === lang;
  }

  public changeChapter(chapter: string): void {
    this.chapter.changeChapter(chapter, this.route.snapshot.url)
  }

  public isChapterDisabled(chapter: string): boolean {
    return this.chapter.getCurrentChapter() === chapter;
  }
}
<rds-header searchPosition="none">
    <rds-divider rds-header-start-divider></rds-divider>
    <rds-header-app-name>{{ 'title' | translate }} {{ env }}</rds-header-app-name>
    <rds-header-navigation-primary>
      <rds-header-link *ngFor="let lang of langs"
        [active]="isLangDisabled(lang)"
        (toggle)="changeLang(lang)"
      >{{ lang }}</rds-header-link>
    </rds-header-navigation-primary>
    <rds-header-navigation-secondary>
      <rds-header-link *ngFor="let chapter of chapters"
        [active]="isChapterDisabled(chapter)"
        (toggle)="changeChapter(chapter)"
      >{{ chapter }}</rds-header-link>
    </rds-header-navigation-secondary>
    <rds-header-actions>
      <rds-avatar>
        <span rds-avatar-initials>AB</span>
      </rds-avatar>
    </rds-header-actions>
</rds-header>
<div class="content">
  <rds-sidenav>
      <app-navigation></app-navigation>
      <app-tree-navigation></app-tree-navigation>
  </rds-sidenav>
  <div class="content-wrapper">
    <router-outlet></router-outlet>
  </div>
</div>
